changeSet.sql:
DELETE FROM reaction r
WHERE r.entity_type = 'POST'
  AND NOT EXISTS (SELECT 1 FROM post p WHERE p.id = r.entity_id);

DELETE FROM reaction r
WHERE r.entity_type = 'COMMENT'
  AND NOT EXISTS (SELECT 1 FROM comment c WHERE c.id = r.entity_id);

            CREATE OR REPLACE FUNCTION reaction_parent_exists()
RETURNS trigger AS $$
            BEGIN
  IF NEW.entity_type = 'POST' THEN
    IF NOT EXISTS (SELECT 1 FROM post WHERE id = NEW.entity_id) THEN
      RAISE EXCEPTION 'Parent post % does not exist', NEW.entity_id;
            END IF;
  ELSIF NEW.entity_type = 'COMMENT' THEN
    IF NOT EXISTS (SELECT 1 FROM comment WHERE id = NEW.entity_id) THEN
      RAISE EXCEPTION 'Parent comment % does not exist', NEW.entity_id;
            END IF;
            END IF;
            RETURN NEW;
            END;
$$ LANGUAGE plpgsql;

DROP TRIGGER IF EXISTS trg_reaction_parent_exists ON reaction;
CREATE TRIGGER trg_reaction_parent_exists
    BEFORE INSERT OR UPDATE ON reaction
                         FOR EACH ROW EXECUTE FUNCTION reaction_parent_exists();
